import { useRouteError } from "react-router-dom";
import PageContent from "./components/PageContent";
function ErrorPage() {
  const error = useRouteError();

  let title = "에러뜸";
  let message = "오류뜸";

  if (error.status === 500) {
    message = error.data.message;
  }
  if (error.status === 404) {
    title = "페이지없음";
  }
  return (
    <PageContent title="에러뜸">
      <p>안좋아</p>
    </PageContent>
  );
}
export default ErrorPage;
